from tkinter import *
import random

class Bot_manager:

    def __init__(self, master):

        self.employees_min = 0
        self.employees_max = 0
        self.perfomance_min = 0
        self.perfomance_max = 0
        self.tasks_quantity_min = 0
        self.tasks_quantity_max = 0
        self.complexity_min = 0
        self.complexity_max = 0
        self.m = 0
        self.s = 0
        self.timer = 0
        self.timer_now = self.timer
        self.timer_state = False
        self.flag = 0
        self.epoch = 0
        self.employees = []
        self.employees_library = ''
        self.tasks = []
        self.tasks_library = ''
        self.current_results_list = []
        self.completed_tasks_list = []

        master.title('Bot_manager')
        master.geometry('500x500+50+50')

        self.timer_window = Label(text='00:00', width=5)
        self.timer_window.place(relx=0.02, rely=0.025, relwidth=0.225, relheight=0.05)

        new_button = Button(text='New', command=self.new_button_function)
        pause_button = Button(text='Pause', command=self.timer_pause)
        settings_button = Button(text='Settings', command=self.settings_window_call)
        new_button.place(relx=0.265, rely=0.025, relwidth=0.225, relheight=0.05)
        pause_button.place(relx=0.51, rely=0.025, relwidth=0.225, relheight=0.05)
        settings_button.place(relx=0.755, rely=0.025, relwidth=0.225, relheight=0.05)

        name_frame_1 = Label(text='Employees')
        name_frame_2 = Label(text='Completed tasks')
        name_frame_1.place(relx=0.02, rely=0.09)
        name_frame_2.place(relx=0.02, rely=0.41)

        frame_1 = Frame()
        frame_2 = Frame()
        frame_1.place(relx=0.02, rely=0.15, relwidth=0.96, relheight=0.25)
        frame_2.place(relx=0.02, rely=0.47, relwidth=0.96, relheight=0.505)

        scroll_list_1_x = Scrollbar(frame_1, orient=HORIZONTAL)
        scroll_list_1_x.pack(side=BOTTOM, fill=X)

        scroll_list_1_y = Scrollbar(frame_1)
        scroll_list_1_y.pack(side=RIGHT, fill=Y)

        scroll_list_2_x = Scrollbar(frame_2, orient=HORIZONTAL)
        scroll_list_2_x.pack(side=BOTTOM, fill=X)

        scroll_list_2_y = Scrollbar(frame_2)
        scroll_list_2_y.pack(side=RIGHT, fill=Y)

        self.list_1 = Listbox(frame_1, selectmode=EXTENDED, xscrollcommand=scroll_list_1_x.set, yscrollcommand=scroll_list_1_y.set)
        self.list_2 = Listbox(frame_2, selectmode=EXTENDED, xscrollcommand=scroll_list_2_x.set, yscrollcommand=scroll_list_2_y.set)
        self.list_1.pack(expand=True, fill=BOTH)
        self.list_2.pack(expand=True, fill=BOTH)
        self.list_1.bind('<Button-1>', self.tasks_window_call)


        scroll_list_1_x.config(command=self.list_1.xview)
        scroll_list_1_y.config(command=self.list_1.yview)
        scroll_list_2_x.config(command=self.list_2.xview)
        scroll_list_2_y.config(command=self.list_2.yview)

